mapper.select:
select count(*) AS pages from tb_treasure where 1 = 1
AND t_dynasty = #{tDynasty,jdbcType=INTEGER}
AND isdelte = 0

SELECT t_id,t_cover_url,t_name,t_number,t_dynasty FROM `tb_treasure` WHERE 1 = 1 AND isdelte != 1
AND t_dynasty = #{tDynasty,jdbcType=INTEGER}
AND t_name like "%"#{tName,jdbcType=VARCHAR}"%"
LIMIT #{begain,jdbcType=INTEGER},#{end,jdbcType=INTEGER}

	select 
 	t_id,
  t_name
  
	FROM tb_treasure 
	WHERE t_dynasty = #{dynasty,jdbcType=INTEGER}
	AND t_id < #{tId,jdbcType=BIGINT}
	AND isdelte = 0
	 
ORDER BY t_id 
DESC LIMIT 1;

select 
  t_id,
  t_name

	
	FROM tb_treasure 
	WHERE t_dynasty = #{dynasty,jdbcType=INTEGER}
	AND t_id > #{tId,jdbcType=BIGINT}
	AND isdelte = 0
 
ORDER BY t_id 
ASC LIMIT 1;
